import React from "react";
import { Card } from "react-bootstrap";

const Upload = ({ handleImage }) => {
  return (
    <Card style={{ width: "18rem" }}>
      <Card.Img variant="top" src="holder.js/100px180" />
      <Card.Body>
        <Card.Title>Send an Image</Card.Title>
        <Card.Text>
          Choose some picture you would like to share with everyone in the
          ChatRoom!
        </Card.Text>
        <Button variant="primary" onClick={handleImage}>
          Browse
        </Button>
      </Card.Body>
    </Card>
  );
};

export default Upload;

