import React, { useRef, useEffect, useState } from "react";

const ImgUpload = () => {
  const canvas = useRef(null);

  const [start, setStart] = useState([]);
  const [finish, setFinish] = useState([]);
  const [mouseDown, setMouseDown] = useState(false);

  let ctx;

  useEffect(() => {
    ctx = canvas.current.getContext("2d");
  });

  useEffect(() => {
    console.log(start, finish);
    const draw = () => {
      ctx = canvas.current.getContext("2d");
      ctx.strokeStyle = "#065fe3d4";
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.strokeRect(
        start[0],
        start[1],
        finish[0] - start[0],
        finish[1] - start[1]
      );
    };
    if (mouseDown) {
      draw();
    }
  }, [finish]);

  const handleImage = (e) => {
    var reader = new FileReader();
    reader.onload = function (event) {
      var img = new Image();
      img.onload = function () {
        canvas.current.width = img.width;
        canvas.current.height = img.height;
        ctx.drawImage(img, 0, 0);
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(e.target.files[0]);
  };

  const mouseClicked = (e) => {
    setFinish([]);
    setMouseDown(true);

    setStart([e.nativeEvent.offsetX, e.nativeEvent.offsetY]);
  };

  const mouseReleased = (e) => {
    setMouseDown(false);
  };

  const mouseMove = (e) => {
    if (mouseDown) {
      setFinish([e.nativeEvent.offsetX, e.nativeEvent.offsetY]);
    }
  };

  return (
    <React.Fragment>
      <label>Image File:</label>
      <input onChange={handleImage} type="file" name="imageLoader" />

      <canvas
        style={{ maxWidth: "50vw", maxHeight: "70vh" }}
        ref={canvas}
        onMouseDown={mouseClicked}
        onMouseUp={mouseReleased}
        onMouseMove={mouseMove}
      ></canvas>
    </React.Fragment>
  );
};

export default ImgUpload;
