import React, { useRef, useEffect, useState } from "react";

const ImgUpload = () => {
  const canvas = useRef(null);

  const [startX, setStartX] = useState(null);
  const [startY, setStartY] = useState(null);
  const [finishX, setFinishX] = useState(null);
  const [finishY, setFinishY] = useState(null);
  const [width, setWidth] = useState(null);
  const [height, setHeight] = useState(null);
  const [mouseDown, setMouseDown] = useState(false);
  const [ctx, setCtx] = useState({});
  const canvasOffset = canvas.current.getBoundingClientRect();
  const offsetX = canvasOffset.left;
  const offsetY = canvasOffset.top;

  useEffect(() => {
    setCtx(canvas.current.getContext("2d"));
  });

  const handleImage = (e) => {
    var reader = new FileReader();
    reader.onload = function (event) {
      var img = new Image();
      img.onload = function () {
        canvas.current.width = img.width;
        canvas.current.height = img.height;
        ctx.drawImage(img, 0, 0);
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(e.target.files[0]);
  };

  const handleMouseDown = (e) => {
    setMouseDown(true);
    setStartX(parseInt(e.clientX - offsetX));
    setStartY(parseInt(e.clientY - offsetY));
  };

  const handleMouseUp = (e) => {
    setMouseDown(false);
  };

  const handleMouseMove = (e) => {
    if (mouseDown) {
      setFinishX(parseInt(e.clientX - offsetX));
      setFinishY(parseInt(e.clientY - offsetY));
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      setWidth(finishX - startX);
      setHeight(finishY - startY);

      ctx.strokeRect(startX, startY, width, height);
    }
  };

  return (
    <React.Fragment>
      <label>Image File:</label>
      <input onChange={handleImage} type="file" name="imageLoader" />

      <canvas
        style={{ maxWidth: "50vw", maxHeight: "70vh" }}
        ref={canvas}
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onMouseMove={handleMouseMove}
      ></canvas>
    </React.Fragment>
  );
};

export default ImgUpload;
