import React, { useRef, useEffect, useState } from "react";

const ImgUpload = () => {
  const canvas = useRef(null);
  const canvasimg = useRef(null);

  const [start, setStart] = useState([]);
  const [finish, setFinish] = useState([]);
  const [mouseDown, setMouseDown] = useState(false);
  const [ctxImg, setCtxImg] = useState({});
  const [ctx, setCtx] = useState({});

  useEffect(() => {
    setCtxImg(canvasimg.current.getContext("2d"));
    setCtx(canvas.current.getContext("2d"));
  }, []);

  const handleImage = (e) => {
    var reader = new FileReader();
    reader.onload = function (event) {
      var img = new Image();
      img.onload = function () {
        canvasimg.current.width = img.width;
        canvasimg.current.height = img.height;
        canvas.current.width = img.width;
        canvas.current.height = img.height;
        ctxImg.drawImage(img, 0, 0);
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(e.target.files[0]);
  };

  const mouseClicked = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setFinish([]);
    setMouseDown(true);
    setStart([e.nativeEvent.offsetX, e.nativeEvent.offsetY]);
  };

  const mouseReleased = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setMouseDown(false);
  };

  const mouseMove = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (!mouseDown) {
      return;
    }

    setFinish([e.nativeEvent.offsetX, e.nativeEvent.offsetY]);
    ctx.clearRect(0, 0, canvas.current.width, canvas.current.height);
    ctx.strokeStyle = "#065fe3d4";

    const startx = start[0];
    const starty = start[1];
    const width = finish[0] - start[0];
    const height = finish[1] - start[1];

    ctx.strokeRect(startx, starty, width, height);
  };

  return (
    <React.Fragment>
      <label>Image File:</label>
      <input onChange={handleImage} type="file" name="imageLoader" />
      <canvas
        ref={canvasimg}
        style={{ position: "absolute", left: "0", top: "0", zIndex: "0" }}
      ></canvas>
      <canvas
        ref={canvas}
        onMouseDown={mouseClicked}
        onMouseUp={mouseReleased}
        onMouseMove={mouseMove}
      ></canvas>
    </React.Fragment>
  );
};

export default ImgUpload;
