import React, { useRef, useEffect, useState } from "react";

const ImgUpload = () => {
  //   var imageLoader = document.getElementById("imageLoader");
  //   imageLoader.addEventListener("change", handleImage, false);
  const canvas = useRef(null);
  //   var ctx = canvas.getContext("2d");

  const [start, setStart] = useState([]);
  const [finish, setFinish] = useState([]);
  const [mouseDown, setMouseDown] = useState(false);

  let ctx;

  useEffect(() => {
    ctx = canvas.current.getContext("2d");
  });

  const handleImage = (e) => {
    var reader = new FileReader();
    reader.onload = function (event) {
      var img = new Image();
      img.onload = function () {
        canvas.current.width = img.width;
        canvas.current.height = img.height;
        ctx.drawImage(img, 0, 0);
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(e.target.files[0]);
  };

  const draw = (e) => {
    // console.log(e);
    ctx = canvas.current.getContext("2d");
    ctx.strokeRect(100, 100, -50, -50);
  };

  const mouseClicked = (e) => {
    // console.log(canvas);
    setMouseDown(true);
    setStart([e.nativeEvent.offsetX, e.nativeEvent.offsetY]);
    // const width = canvas.current.clientWidth;
    // const height = canvas.current.clientHeight;
    // console.log(`la altura es ${width}, el ancho es ${height}`);
  };

  const mouseReleased = (e) => {
    setMouseDown(false);
    // console.log(e);
  };

  const mouseMove = (e) => {
    console.log(e);
    MouseDown
      ? setFinish([e.nativeEvent.offsetX, e.nativeEvent.offsetY])
      : null;
  };

  return (
    <React.Fragment>
      <label>Image File:</label>
      <input
        onChange={handleImage}
        type="file"
        id="imageLoader"
        name="imageLoader"
      />
      <canvas
        ref={canvas}
        onClick={draw}
        onMouseDown={mouseClicked}
        onMouseUp={mouseReleased}
        onMouseMove={mouseMove}
      ></canvas>
    </React.Fragment>
  );
};

export default ImgUpload;
