import React from "react";
import { Card, Button } from "react-bootstrap";
import "./Upload.css";

const Upload = ({ handleImage }) => {
  return (
    <Card className="uploadCard text-center">
      <Card.Img className="uploadImg" variant="top" src="/images/upload2.svg" />
      <Card.Body>
        <Card.Title>Send an Image</Card.Title>
        <Card.Text>
          Choose some picture you would like to share with everyone in the
          ChatRoom!
        </Card.Text>
        <Button className="uploadBtn" variant="primary">
          <input onChange={handleImage} type="file" name="imageLoader" />
        </Button>
      </Card.Body>
    </Card>
  );
};

export default Upload;

