import React from "react";

import "./Input.css";

const Input = () => {
  return (
    <input
      value={message}
      onChange={(e) => setMessage(e.target.value)}
      onKeyPress={(e) => (e.key === "Enter" ? sendMessage(e) : null)}
    />
  );
};

export default Input;
